import React from 'react'
import Square from './Square'

class Board extends React.Component {
  renderSquare (i) {
    return <Square
      value={this.props.squares[i]}
      onClick={() => this.props.onClick(i)}
    />
  }

  makeRows (mainSquare) {
    for (var i = 1; i = 3; i++) {
      var row = document.createElement('div')
      row.className = 'board-row'
      for (var j = 1; j = 3; j++) {
        this.renderSquare(i * j - 1)
      }
      mainSquare.appendChild(row)
    }
    return mainSquare
  }

  render () {
    var mainSquare = document.createDocumentFragment()
    mainSquare = this.makeRows(mainSquare)
    return (
      mainSquare
      /* </div><div>
        <div className = "board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className = "board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className = "board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div> */
    )
  }
}
export default Board
